css:
.table * {
    font-family: 'Times New Roman', Times, serif;
}

.table>thead * {
    font-size: 12px;
}

.main-table>thead *,
.main-table>tfoot * {
    font-size: 12px;
}

.table>tbody * {
    font-size: 12px;
}

.table {
    width: 100%;
    gap: 0px !important;
    border-spacing: 0px !important;
    margin: 0px !important;
}


table>thead>tr>th,
table>thead>tr>td,
table>tbody>tr>th,
table>tbody>tr>td,
table>tfoot>tr>th,
table>tfoot>tr>td {
    /* padding-left: 5px !important;
    padding-right: 5px !important; */
    padding: 0px 5px !important;
    text-align: start;
}

.company-header {
    display: flex;
    position: relative;
    justify-content: center;
    align-items: center;

}

.company-header .logo {
    height: 30px;
    width: 120px;
    position: absolute;
    top: 5px;
    left: 0px;
}

.company {
    font-size: 16px !important;
    margin: 3px 0px !important;
    padding: 6px 0px;
    text-align: center !important;
}

p {
    margin: 0px;
}

.border-top {
    border-style: solid !important;
    border-top-width: 1px !important;
}

.border-bottom {
    border-style: solid !important;
    border-bottom-width: 1px !important;
}

.border-left {
    border-style: solid !important;
    border-left-width: 1px !important;
}

.border-right {
    border-style: solid !important;
    border-right-width: 1px !important;
}

.border-top-0 {
    border-top-width: 0px !important;
}

.border-bottom-0 {
    border-bottom-width: 0px !important;
}

.border-left-0 {
    border-left-width: 0px !important;
}

.border-right-0 {
    border-right-width: 0px !important;
}

.border-dark {
    border-color: rgb(35, 35, 35);
}

.border-black {
    border-color: rgb(0, 0, 0);
}

.border {
    border-width: 1px !important;
    border-style: solid !important;
}

.text-center {
    text-align: center !important;
}

.text-start {
    text-align: start !important;
}

.text-end {
    text-align: end !important;
}

.fs-italic {
    /* font-style: italic !important; */
}

table>tbody>tr>td.sno {
    /* white-space: nowrap !important; */
    width: 3% !important;
}

table>tbody>tr>td.sod,
table>tbody>tr>th.sod {
    /* white-space: nowrap !important; */
    width: 8% !important;
}

table>tbody>tr>td.sod-sno,
table>tbody>tr>th.sod-sno {
    /* white-space: nowrap !important; */
    width: 4% !important;
}

table>tbody>tr>td.docs {
    /* white-space: nowrap !important; */
    width: 55% !important;
    font-weight: 400;
}

table>tbody>tr>td.check {
    /* white-space: nowrap !important; */
    width: 10% !important;
    font-weight: 600;
}

table>tbody>tr>td.remarks {
    /* white-space: nowrap !important; */
    width: 25% !important;
}

.text-rotate {
    display: flex;
    width: 100%;
    height: auto;
    justify-content: center;
    align-items: center;
    writing-mode: tb-rl !important;
    transform: rotate(-180deg) !important;
    padding: 10px 0px !important;
}

.bg-light {
    background-color: rgb(180, 180, 180) !important;
    -webkit-print-color-adjust: exact !important;
}

/* print settings */

.table {
    /* table-layout: fixed !important; */
}



.table>tbody>tr>td,
.table>tbody>tr>th,
.table>tfoot>tr>td,
.table>tfoot>tr>th,
.table>thead>tr>td,
.table>thead>tr>th {
    padding: 0px;
    line-height: 1.3;
    vertical-align: middle !important;
    border: none;
    height: 10px !important;
    max-height: 10px !important;
}

.print-format .table {
    margin: 0px !important;
}


.print-format th {
    background-color: #eee !important;
    border-bottom: none !important;
    border-bottom-width: 1px !important;
    border-color: rgb(0, 0, 0) !important;
    border-style: solid !important;
}

.print-format td,
.print-format th {
    vertical-align: top !important;
    padding: 0px 5px !important;
}
